import Card from '../card/Card';
import { connect, useDispatch, useSelector } from 'react-redux';
import { filterCards, orderCards } from '../../redux/actions.js';
import { useState } from 'react';
import style from './favorites.module.css'



  const  Favorites =()=>{

    // const [aux, setAux] = useState(false); // esta linea es para forzar el renderizado 

  const dispatch = useDispatch();
  const myFavorites = useSelector((state) => state.myFavorites);

  const handleOrder = (event)=>{
    dispatch(orderCards(event.target.value));
      setAux(true); // esta linea es para forzar el renderizado 
  };

  const handleFilter = (event)=>{
        
      dispatch(filterCards(event.target.value));
    };

  // const handleReset = () => {
  //   dispatch(resetFavorites())
  // };  
    
// <select onChange={handleFilter} placeholder ="Gender">
// {[ "Male", "Female", "unknown", "Genderless"].map((gender)=> (
// <option value={gender}>{gender}</option> ))}
//</select>  // de esta manera tambien se puede colocar para los filtros de las types del pi de pokemons

// se puede colocar dentro del select y fuera del map disabled selected value ="" para que se borre la seleccion
// del ordenamiento de forma automática.
  return (
    <div>
    <div className={style.selectBtn}>
    <select onChange={handleOrder} >
      <option value="A">Ascendente</option>
      <option value="D">Descendente</option>
    </select>
    <select onChange={handleFilter}>
      <option value="Male">Male</option>
      <option value="Female">Female</option>
      <option value="Genderless">Genderless</option>
      <option value="unknown">unknown</option>
      <option value="allCharacters">allCharacters</option>
    </select>

    </div>
    <div className={style.cardFav}>
     {
      myFavorites?.map((fav)=>{
        return (
          <Card 
                   key={fav.id}
                   id={fav.id}
                   name={fav.name}
                   status={fav.status}
                   species={fav.species}
                   gender={fav.gender}
                   origin={fav.origin?.name}
                   image={fav.image}
                   onClose={fav.onClose}
                   />
        )
        })
    }
    </div>
    </div>
  )
}
//  <Card characters={myFavorites} /> así se puede renderizar en la Card, en vez de lo de arriba

const mapStateToProps = (state)=>{
  return {
    myFavorites: state.myFavorites
 }
}
export default connect(mapStateToProps, null)(Favorites);
