import { Link, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import style from './card.module.css'
import { useSelector, useDispatch} from 'react-redux';
import { addFavorite, removeFavorite } from '../../redux/actions';


 function Card(props) {

   const navigate = useNavigate();
    const dispatch = useDispatch();
    const myFavorites = useSelector((state) => state.myFavorites);
    const { onClose } = props
    const { name, species, gender, image, id } = props.character
    const [isFav, setIsFav] = useState(false);

   // const [ closeBt, setCloseBt] = useState(true);

   // useEffect(()=> {
   //    if(!onClose){
   //       setCloseBt(false);
   //    }
   // },[]);

   const handleFavorite = (arg)=> {
      if(isFav) {
         setIsFav(false);
         dispatch(removeFavorite(arg));
      } else {
         setIsFav(true);
         dispatch(addFavorite(arg));
      }
   };

   //if (data.name && !characters.find((charater) => charater.id === data.id))

   useEffect(() => {
      myFavorites.forEach((fav) => {
         if (fav.id === id) {
            setIsFav(true);
         } 
      });
   }, [id, myFavorites]);
   function navigateHandler() {
      navigate(`/detail/${id}`);
   }
   
   return (
      <div className={style.cardConten}>
         <div className={style.imageConten}>
         <button className={style.fav} onClick={handleFavorite}>{isFav ? '‚ù§Ô∏è' : 'ü§ç'}</button>      
          <img src={image} alt={name}/>         
         </div>

        <div className={style.atrib}>
         <div className={style.closeButn}>
         {!pathname.includes("/favorites") && (
           <button onClick={() => {onClose(id)}}>X</button>
           )}
         <Link to={`/detail/${id}`}><h2 >{name}</h2></Link>
         </div>
         <h2>Especie: {species}</h2>
         <h2>G√©nero: {gender}</h2>      
         </div>
      </div>
   );
};



const mapDispatchToProps = (dispatch) =>{
   return {
      addFavorite: (character) => {
         dispatch(addFavorite(character));
      },

      removeFavorite: (id) => {
         dispatch(removeFavorite(id));
      },
   }
};

const mapStateToProps = (state)=>{
   return {
      myFavorites: state.allCharactersFav,
   }
}

export default  connect(mapStateToProps, mapDispatchToProps)(Card);
