import { NavLink, useLocation } from 'react-router-dom';
import { useState, useEffect } from 'react';
import style from './card.module.css'
import { useSelector, useDispatch} from 'react-redux';
import { addFavorite, removeFavorite } from '../../redux/actions';


 function Card({character, onClose}) {

    const dispatch = useDispatch();
    const myFavorites = useSelector((state) => state.myFavorites);
    const location = useLocation();
        const [isFav, setIsFav] = useState(false);

   // const [ closeBt, setCloseBt] = useState(true);

   // useEffect(()=> {
   //    if(!onClose){
   //       setCloseBt(false);
   //    }
   // },[]);

   const handleFavorite = (arg)=> {
      if(isFav) {
         setIsFav(false);
         dispatch(removeFavorite(arg));
      } else {
         setIsFav(true);
         dispatch(addFavorite(arg));
      }
   };

   //if (data.name && !characters.find((charater) => charater.id === data.id))

   useEffect(() => {
      myFavorites.forEach((fav) => {
         if (fav.id === id) {
            setIsFav(true);
         } 
      });
   }, [id, myFavorites]);
   
   
   return (
      <div className={style.cardConten}>
         <div className={style.imageConten}>
           {isFav ? (<button onClick={() => { handleFavorite(id) }}>‚ù§Ô∏è</button>)
               : (<button onClick={() => { handleFavorite(props.character) }}>ü§ç</button>)}
          <img src={image} alt='imagen'/>         
         </div>

        <div className={style.atrib}>
         <div className={style.closeButn}>
         {!location.pathname === "/home" && (
           <button onClick={() => {onClose(id); dispatch(removeFavorite(id))}}>X</button>
           )}
         <NavLink to={`/detail/${id}`}><h2 >{name}</h2>
         </NavLink>
         </div>
         <h2>Especie: {species}</h2>
         <h2>G√©nero: {gender}</h2>      
         </div>
      </div>
   );
};


export default Card;
