import './App.css';
import Cards from './components/cards/Cards.jsx';
import Nav from './components/nav/Nav.jsx';

import { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import About from './components/about/About.jsx';
import Detail from './components/detail/Detail.jsx';
import Error from './components/error/Error.jsx';
import LoginForm from './components/loginForm/LoginForm';
import Favorites from './components/favorites/Favorites.jsx';
import { removeFavorite } from './redux/actions';
import { useDispatch } from 'react-redux';
import axios from 'axios';




// const EMAIL = 'luvillogas@gmail.com';
// const PASSWORD = 'hola1379';

function App() {

   const [characters, setCharacters] = useState([]);
   const location = useLocation();
   const navigate = useNavigate();
   let [access, setAccess] = useState(false);
   const dispatch = useDispatch();

   
   const onSearch= async (id)=> {

      try {
         const { data } = await axios(`http://localhost:3001/rickandmorty/character/${id}`);

         if (data.name && !characters.find((charater) => charater.id === data.id)) {
               setCharacters((oldChars) => [...oldChars, data]);
            };
            
         } catch (error) {
         alert('¡No hay personajes con este ID!');
         
      }      
   };
   // id es lo q escribe el usuario
   // axios es una libreria, similar al fetch
   // se instala en la carpeta npm i axios
   // !characters.find((charater) => charater.id === data.id), no se repita los elem llamados

   const onClose = (id) => {

      let deleted = characters.filter((elem) => elem.id !== Number(id))

      setCharacters(deleted);
      dispatch(removeFavorite(id));

   }

   const generateRandomCharacter = async () => {
  
      let random = Math.floor(Math.random() * 826) + 1;
      // toFixed(); devuelve nro. redondeado, si colocas algn nro dentro del parentesis, devuelve entero con cantidad de nros decimales.
      //826, al agregar data colocar la cantidad
      
      const characterExists = characters.some(
         (character) => character.id === random
       );

       if (characterExists) {
         return generateRandomCharacter();
       }
       return random;
   };
   const randomHandler = () => {
      let randomID = generateRandomCharacter();
      onSearch(randomID);
    };

    // function loginHandler(userData) {
   //    if (userData.password === PASSWORD && userData.email === EMAIL) {
   //       setAccess(true);
   //       navigate('/home');
   //    }
   // } esta función estoy reemplazando por esta: (M3)
   const URL = 'http://localhost:3001/rickandmorty/login/';
   
   const loginHandler = async (userData) => {
      try {
         const { email, password } = userData;
         const { data } = await axios(URL + `?email=${email}&password=${password}`);
         const { access } = data;
         setAccess(access);
         access && navigate('/home');
      } catch (error) {
         console.log(error.message);
      }
      
      };

   useEffect(() => {
      !access && navigate('/');
   }, [access, navigate]);



   return (
      <div className='App'>
         <div className="video-background">
        <video loop muted autoPlay playsInline>
          <source src="/media/background-video.mp4" type="video/mp4" />
          Su navegador no soporta la etiqueta de vídeo.
        </video>
      </div>

         {location.pathname !== "/" && (<Nav onSearch={onSearch} random={randomHandler} setAccess={setAccess}/>)}

         <Routes>
         <Route exact path='/' element={<LoginForm loginHandler={loginHandler} />}/>
         <Route exact path='/home' element={<Cards characters={characters} onClose={onClose} />} />
         <Route exact path='/about' element={<About />} />
         <Route exact path='/favorites' element={<Favorites />} />
         <Route exact path='/detail/:id' element={<Detail />}/>
         <Route path="/*" component={<Error />} />
         </Routes>
           
      </div>
   );
}



export default App;
